export default class AccordionTabSliderView{constructor({container,type,viewOptions},model){this._model=model;this.container=container;this.type=type;this.options=viewOptions;this._createDataIndex(this.container);this._progressBars=this.container.querySelectorAll("progress");this._model.subscribe(this.updateView.bind(this))}_createDataIndex(container){Array.from(container.children).forEach((child,index)=>{child.dataset.index=index})}updateView(direction,progress){if(progress){this._updateProgressBar(progress)}else{Array.from(this.container.children).forEach(child=>{this._resetView(child);this._setActiveView(child)})}}_resetView(view){view.classList.remove("active-item");Array.from(view.children).forEach(child=>{if(child.classList.contains("info-slide"))child.classList.remove("active-info-item")});this._resetProgressBar(view)}_resetProgressBar(view){const progressBar=view.querySelector("progress");if(progressBar)progressBar.value=0}_setActiveView(element){if(element&&element.dataset.index==this._model.currentIndex){element.classList.add("active-item");Array.from(element.children).forEach(child=>{if(child.classList.contains("info-slide"))child.classList.add("active-info-item")})}}_updateProgressBar(thisPercentage){const progressBar=this._progressBars[this._model.currentIndex];if(progressBar)progressBar.value=thisPercentage}}