export default class SliderModel{constructor(numItems){this.currentIndex=-1;this.subscribers=[];this.items=numItems}subscribe(callback){this.subscribers.push(callback)}notifySubscribers(direction,value){this.subscribers.forEach(callback=>callback(direction,value))}goToNext(){this.currentIndex=(this.currentIndex+1)%this.items;this.notifySubscribers(1)}goToPrev(){this.currentIndex=(this.currentIndex-1+this.items)%this.items;this.notifySubscribers(-1)}updateProgress(progress){this.notifySubscribers(0,progress)}getCurrentItem(){return this.currentIndex}setCurrentIndex(index){this.currentIndex=index;this.notifySubscribers()}}