export default class SwipeListener{constructor(element,touchChangeThreshold){this.element=element;this.touchChangeThreshold=touchChangeThreshold;this.startingX=0;this.startingY=0}_getCoordinates(e){if(e.type=="touchend"){return{x:e.changedTouches[0].pageX,y:e.changedTouches[0].pageY}}if(e.touches&&e.touches.length>0){return{x:e.touches[0].clientX,y:e.touches[0].clientY}}else{return{x:e.x,y:e.y}}}start(e){const coordinates=this._getCoordinates(e);this.startingY=coordinates.y;this.startingX=coordinates.x}end(e){this.element.style.transition="";const coordinates=this._getCoordinates(e);let change=this.startingX-coordinates.x;if(Math.abs(change)>this.touchChangeThreshold){return change>0?1:-1}else{return 0}}}