import PopupGenerator from"/wp-content/themes/Tipalti-Synergy/dist/js/PopupGenerator.min.js?v=1.0.0";class tcbPopup{constructor(){this.popupWrappers=document.querySelectorAll(".popup-wrapper");if(!this.popupWrappers||this.popupWrappers.length===0){return}this.replaceLinksWithPopup();this.init()}replaceLinksWithPopup(){const getStartedLinks=document.querySelectorAll('a[href="/get-started"], a[href="/get-started/"]');if(!getStartedLinks||getStartedLinks.length===0){return}getStartedLinks.forEach(link=>{link.setAttribute("href","javascript:void(0)");if(!link.classList.contains("js-get-started-popup"))link.classList.add("js-get-started-popup")})}init(){this.popupWrappers.forEach(popupWrapper=>{let uniqueId=popupWrapper.getAttribute("data-unique-id");let relatedContentId=popupWrapper.getAttribute("data-related-content-id");let customClass=popupWrapper.getAttribute("data-custom-class");let activationButtonId=popupWrapper.getAttribute("data-activation-id");let popupContentHTML=null;popupContentHTML=document.getElementById(relatedContentId);popupWrapper.querySelector(".wp-block-tcb-gated-video-player-content")?.classList.add("is-inside-popup");window["tgtRenderPopup_"+uniqueId]=()=>{let popupOptions={popupContentHTML:popupContentHTML,customClass:customClass};let popupObj=new PopupGenerator(popupOptions);popupObj.activatePopup()};let clickableElement=document.querySelectorAll(activationButtonId);if(typeof clickableElement.length!=="undefined"){for(let i=0;i<clickableElement.length;i++){clickableElement[i].addEventListener("click",function(){window["tgtRenderPopup_"+uniqueId]()})}}});document.dispatchEvent(new Event("popupReady"))}}new tcbPopup;